function switchBurger(){if(document.getElementById("navbox-btn")){let e=document.getElementById("navbox-btn"),t=document.getElementById("target");e.addEventListener("click",()=>{e.classList.toggle("open"),t.classList.toggle("hidden")})}}function isInViewport(e){const t=e.getBoundingClientRect();return t.top<(window.innerHeight||document.documentElement.clientHeight)}function showPic(){setTimeout(function(){for(var t=document.getElementsByClassName("imag"),e=0;e<t.length;e++)isInViewport(t[e])&&t[e].getAttribute("data-toset")&&(t[e].src=t[e].dataset.toset,delete t[e].dataset.toset)},100)}window.addEventListener("scroll",function(){showPic()}),showPic()