function getcolorscheme(){let storageColorScheme=localStorage.getItem("lightDarkMode");let element=document.getElementById('lightDarkMode');let targetDiv=document.getElementById('lightDarkOptions');let targets=targetDiv.getElementsByTagName('div');let screen=document.getElementById('is-open');if(storageColorScheme==null||storageColorScheme=='Auto'){switchMode('Auto')}
if(storageColorScheme=='Light'){element.firstElementChild.setAttribute("data-icon",'sun')
element.firstElementChild.classList.remove('fa-adjust')
element.firstElementChild.classList.add('fa-sun')}else if(storageColorScheme=='Dark'){element.firstElementChild.setAttribute("data-icon",'moon')
element.firstElementChild.classList.remove('fa-adjust')
element.firstElementChild.classList.add('fa-moon')}
element.addEventListener('click',()=>{targetDiv.classList.toggle('hidden')
screen.classList.toggle('hidden')})
for(let target of targets){target.addEventListener('click',()=>{let icon=switchMode(target.getAttribute('type'))
let old_icon=element.firstElementChild.getAttribute("data-icon")
element.firstElementChild.setAttribute("data-icon",icon)
element.firstElementChild.classList.remove('fa-'+old_icon)
element.firstElementChild.classList.add('fa-'+icon)
localStorage.setItem("lightDarkMode",target.getAttribute('type'))
targetDiv.classList.toggle('hidden')
screen.classList.toggle('hidden')})}
screen.addEventListener('click',()=>{targetDiv.classList.toggle('hidden')
screen.classList.toggle('hidden')})}
function switchMode(mode){let icon=''
switch(mode){case 'Light':window.matchMedia("(prefers-color-scheme: dark)").removeEventListener('change',switchDarkMode)
icon='sun'
document.getElementsByTagName('html')[0].classList.remove('dark')
break
case 'Dark':window.matchMedia("(prefers-color-scheme: dark)").removeEventListener('change',switchDarkMode)
icon='moon'
document.getElementsByTagName('html')[0].classList.add('dark')
break
case 'Auto':icon='adjust'
const isDarkMode=window.matchMedia("(prefers-color-scheme: dark)")
switchDarkMode(isDarkMode)
window.matchMedia("(prefers-color-scheme: dark)").addEventListener('change',switchDarkMode)
break}
return icon}
function switchDarkMode(e){if(e.matches){document.getElementsByTagName('html')[0].classList.add('dark')}else{document.getElementsByTagName('html')[0].classList.remove('dark')}}
function notitle(){var links=document.links;for(var i=0;i<links.length;i++){var a=links[i];if(a.title!==''){a.addEventListener('mouseover',titleoff);a.addEventListener('mouseout',titleon);}}}
function titleoff(){var title=this.title;this.title='';this.setAttribute("temptitle",title);}
function titleon(){var title=this.getAttribute("temptitle");this.title=title;this.removeAttribute("temptitle");}
$('.pageMenu').click(function(){var targ=$(this).attr('scrllto');$("html, body").animate({scrollTop:$(targ).offset().top+10},700,"swing");});$('.mainMenu, .submenu>div').click(function(){$('.mainMenu, .submenu div').removeClass('active');$(this).addClass('active');});$('.mainMenu').click(function(){var targ='.'+$(this).attr('data-filter');$('.submenu').addClass('hidden');$(targ).removeClass('hidden');});$('input').keydown(function(){$("html, body").animate({scrollTop:$("#activities").offset().top+10},500,"swing");});var filterizd=$('.filtr-container').filterizr({animationDuration:0.8,controlsSelector:'',delay:8,delayMode:'progressive',easing:'ease-in-out',filter:'all',filterOutCss:{opacity:0,transform:'scale(0.01)'},filterInCss:{opacity:1,transform:'scale(1)'},gridItemsSelector:'.filtr-container',gutterPixels:0,layout:'sameWidth',});filterizd.on('filteringStart.Filterizr',function(){var element=document.getElementById('activities');element.scrollIntoView({behavior:"smooth"});});